mapper.select:
SELECT a.seq ,a.codeGroup_seq ,b.name_ko ,a.codeNum ,a.anotherCode ,a.cdname_ko ,a.name_eng ,a.order ,a.regDate ,a.modDate ,a.useNY ,a.delNY FROM Code a inner join CodeGroup b on b.seq = a.codeGroup_seq WHERE 1=1
AND a.delNY = #{shDelNy}
AND a.regDate BETWEEN #{shstartDate} AND #{shendDate} AND a.modDate BETWEEN #{shstartDate} AND #{shendDate}
AND a.codeGroup_seq = #{shValue} AND b.name_ko LIKE CONCAT('%',#{shValue},'%') AND a.cdname_ko LIKE CONCAT('%',#{shValue},'%') AND a.name_eng LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO Code (
	codeGroup_seq
	, codeNum
	, cdname_ko
	, name_eng
	, regDate
	, useNY
	, delNY
)
VALUES (
	#{codeGroup_seq}
	,#{codeNum}
	,#{cdname_ko}
	,#{name_eng}
	,now()
	,1
	,0
)

	SELECT last_insert_id()
