mapper.sql:
FROM Code a
inner join CodeGroup b on b.seq = a.codeGroup_seq
WHERE 1=1

AND a.delNY = #{shDelNy}

	AND a.regDate BETWEEN #{shstartDate} AND #{shendDate}
	AND a.modDate BETWEEN #{shstartDate} AND #{shendDate}



	AND a.codeNum LIKE CONCAT('%',#{shValue},'%')
	AND b.name_ko LIKE CONCAT('%',#{shValue},'%')
	AND a.cdname_ko LIKE CONCAT('%',#{shValue},'%')
	AND a.name_eng LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO Code (
	codeGroup_seq
	, codeNum
	, cdname_ko
	, name_eng
	, regDate
	, modDate
	, useNY
	, delNY
)
VALUES (
	#{codeGroup_seq}
	, #{codeNum}
	, #{cdname_ko}
	, #{name_eng}
	, #{regDate}
	, now()
	, 1
	, 0
)

	SELECT last_insert_id()

mapper.update:
UPDATE Code
SET
	codeGroup_seq = #{codeGroup_seq}
	, codeNum = #{codeNum}
	, cdname_ko = #{cdname_ko}
	, name_eng = #{name_eng}
	, modDate = now()
WHERE
	seq = #{seq}
