mapper.sql:
FROM CodeGroup a
WHERE 1=1

AND a.delNY = #{shDelNy}

	AND a.regDate BETWEEN #{shstartDate} AND #{shendDate}
	AND a.modDate BETWEEN #{shstartDate} AND #{shendDate}



	AND a.codegroupNum LIKE CONCAT('%',#{shValue},'%')
	AND a.name_ko LIKE CONCAT('%',#{shValue},'%')
	AND a.name_eng LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO CodeGroup (
	seq
	, codegroupNum
	, name_ko
	, name_eng
	, codeamount
	, regDate
	, modDate
	, useNY
	, delNY
)
VALUES (
	#{seq}
	, #{codegroupNum}
	, #{name_ko}
	, #{name_eng}
	, #{codeamount}
	, #{regDate}
	, now()
	, 1
	, 0
)

	SELECT last_insert_id()

mapper.update:
UPDATE CodeGroup
SET
	codegroupNum = #{codegroupNum}
	, name_ko = #{name_ko}
	, name_eng = #{name_eng}
	, modDate = now()
WHERE
	seq = #{seq}
