mapper.select:
SELECT a.seq , a.codeNum , a.name_ko , a.name_eng , (SELECT COUNT(b.codeGroup_seq) FROM Code b WHERE 1=1 AND a.seq = b.codeGroup_seq) AS codeamount , a.regDate , a.modDate , a.delNY FROM CodeGroup a WHERE 1=1
AND a.delNY = #{shDelNy}
AND a.regDate BETWEEN #{shstartDate} AND #{shendDate} AND a.modDate BETWEEN #{shstartDate} AND #{shendDate}
AND a.codeNum LIKE CONCAT('%',#{shValue},'%') AND a.name_ko LIKE CONCAT('%',#{shValue},'%') AND a.name_eng LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO CodeGroup (
	seq
	, codeNum
	, name_ko
	, name_eng
	, codeamount
	, regDate
	, modDate
	, delNY
)
VALUES (
	#{seq}
	, #{codeNum}
	, #{name_ko}
	, #{name_eng}
	, #{codeamount}
	, #{regDate}
	, now()
	, 0
)

	SELECT last_insert_id()

mapper.update:
UPDATE CodeGroup
SET
	seq = #{seq}
	, codeNum = #{codeNum}
	, name_ko = #{name_ko}
	, name_eng = #{name_eng}
	, modDate = now()
WHERE
	seq = #{seq}
