mapper.sql:
FROM item a
WHERE 1=1

AND a.delNY = #{shDelNy}

	AND a.regDate BETWEEN #{shstartDate} AND #{shendDate}
	AND a.modDate BETWEEN #{shstartDate} AND #{shendDate}



	AND a.title LIKE CONCAT('%',#{shValue},'%')
	AND a.email LIKE CONCAT('%',#{shValue},'%')
	AND a.phone LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO member (
	name
	, dob
	, membership
	, email
	, phone
	, regDate
	, delNY
)
VALUES (
	#{name}
	, #{dob}
	, #{membership}
	, #{email}
	, #{phone}
	, now()
	, 0
)

	SELECT last_insert_id()

mapper.update:
UPDATE member
SET
	name = #{name}
	, dob = #{dob}
	, membership = #{membership}
	, email = #{email}
	, phone = #{phone}
	, regDate = #{regDate}
WHERE
	seq = #{seq}
