mapper.sql:
FROM item a inner join Code b ON a.deliverymethod = b.seq WHERE 1=1
AND a.delNY = #{shDelNy}
AND a.modDate BETWEEN #{shstartDate} AND #{shendDate}
AND a.title LIKE CONCAT('%',#{shValue},'%') AND site LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO item (
	title
	, discount
	, price
	, released
	, site
	, shippingfee
	, deliverymethod
	, stock
	, modDate
	, useNY
	, delNY
)
VALUES (
	#{title}
	, #{discount}
	, #{price}
	, #{released}
	, #{site}
	, #{shippingfee}
	, #{deliverymethod}
	, #{stock}
	, now()
	, 1
	, 0
)

	SELECT last_insert_id()

mapper.update:
UPDATE item 
SET
	title = #{title}
	, discount = #{discount}
	, price = #{price}
	, released = #{released}
	, site = #{site}
	, shippingfee = #{shippingfee}
	, deliverymethod = #{deliverymethod}
	, stock = #{stock}
	, modDate = now()
	, useNY = #{useNY}
	, delNY = #{delNY}
WHERE 
	seq = #{seq}

