mapper.sql:
FROM item a
WHERE 1=1

AND a.delNY = #{shDelNy}

	AND a.regDate BETWEEN #{shstartDate} AND #{shendDate}
	AND a.modDate BETWEEN #{shstartDate} AND #{shendDate}



	AND a.title LIKE CONCAT('%',#{shValue},'%')
	AND a.delivery LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO item (
	seq
	, title
	, discount
	, price
	, fee
	, delivery
	, stock
	, useNY
	, delNY
)
VALUES (
	seq
	, #{title}
	, #{discount}
	, #{price}
	, #{fee}
	, #{delivery}
	, #{stock}
	, 1
	, 0
)

	SELECT last_insert_id()

mapper.update:
UPDATE item
SET
	title = #{title}
	, discount = #{discount}
	, price = #{price}
	, fee = #{fee}
	, delivery = #{delivery}
	, stock = #{stock}
	, modDate = #{modDate}
	, useNY = #{useNY}
	, delNY = #{delNY}
WHERE
	seq = #{seq}
