mapper.sql:
FROM member a
	inner join Code b ON a.grade = b.seq
WHERE 1=1

AND a.delNY = #{shDelNy}
AND a.grade = #{shOptionMem}

	AND a.dob BETWEEN #{shstartDate} AND #{shendDate}
	AND a.regDate BETWEEN #{shstartDate} AND #{shendDate}



	AND a.name LIKE CONCAT('%',#{shValue},'%')
	AND a.email LIKE CONCAT('%',#{shValue},'%')
	AND a.phone LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO member (
	name
	, dob
	, id
	, pwd
	, grade
	, email 
	, emailID
	, emailDomain
	, telecom
	, phone
	, gender
	, zipcode
	, addr1
	, addr2
	, addr3
	, validity
	, regDate
	, delNY
)
VALUES (
	#{name}
	, #{dob}
	, #{id}
	, #{pwd}
	, 2
	, #{email}
	, #{emailID}
	, #{emailDomain}
	, #{telecom}
	, #{phone}
	, #{gender}
	, #{zipcode}
	, #{addr1}
	, #{addr2}
	, #{addr3}
	, #{validity}
	, now()
	, 0
)

	SELECT last_insert_id()

mapper.update:
UPDATE member 
SET
	name = #{name}
	, grade = #{grade}
	, dob = #{dob}
	, pwd = #{pwd}
	, gender = #{gender}
	, email = CONCAT(#{emailID},

			'@naver.com')
			'@gmail.com')
			'@hanmail.net')
			'@daum.com')
			'@nate.com')
			'@coocha.com')

	, emailID = #{emailID}
	, emailDomain = #{emailDomain}
	, telecom = #{telecom}
	, phone = #{phone}
	, zipcode = #{zipcode}
	, addr1 = #{addr1}
	, addr2 = #{addr2}
	, addr3 = #{addr3}
	, validity = #{validity}
	, delNY = #{delNY}
WHERE 1=1
	AND seq = #{seq}

UPDATE member 
SET
	id = #{id}
	, pwd = #{pwd}
	, dob = #{dob}
	, gender = #{gender}
	, email = CONCAT(#{emailID},

			'@naver.com')
			'@gmail.com')
			'@hanmail.net')
			'@daum.com')
			'@nate.com')
			'@coocha.com')

	, emailID = #{emailID}
	, emailDomain = #{emailDomain}
	, telecom = #{telecom}
	, phone = #{phone}
	, zipcode = #{zipcode}
	, addr1 = #{addr1}
	, addr2 = #{addr2}
	, addr3 = #{addr3}
	, validity = #{validity}
WHERE 1=1
	AND seq = #{seq}

mapper.delete:
DELETE FROM member
WHERE 1=1
	AND seq = #{seq}

mapper.select:
SELECT seq , id FROM member WHERE 1=1 AND id = #{id}
AND pwd = #{pwd}
AND delNY = 0

