mapper.sql:
FROM member a
WHERE 1=1

AND a.delNY = #{shDelNy}
AND a.grade = #{shOptionMem}

	AND a.dob BETWEEN #{shstartDate} AND #{shendDate}
	AND a.regDate BETWEEN #{shstartDate} AND #{shendDate}



	AND a.name LIKE CONCAT('%',#{shValue},'%')
	AND a.email LIKE CONCAT('%',#{shValue},'%')
	AND a.phone LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO member (
	name
	, dob
	, id
	, pwd
	, email
	, emailID
	, emailDomain
	, phone
	, zipcode
	, address
	, addressDetail
	, addr3
	, regDate
	, delNY
)
VALUES (
	#{name}
	, #{dob}
	, #{id}
	, #{pwd}
	, #{email}
	, #{emailID}
	, #{emailDomain}
	, #{phone}
	, #{zipcode}
	, #{address}
	, #{addressDetail}
	, #{addr3}
	, now()
	, 0
)

	SELECT last_insert_id()

mapper.update:
UPDATE member
SET
	name = #{name}
	, dob = #{dob}
	, id = #{id}
	, pwd = #{pwd}
	, email = #{email}
	, emailID = #{emailID}
	, emailDomain = #{emailDomain}
	, phone = #{phone}
	, zipcode = #{zipcode}
	, address = #{address}
	, addressDetail = #{addressDetail}
	, addr3 = #{addr3}
	, regDate = #{regDate}
WHERE
	seq = #{seq}
