mapper.sql:
FROM member a
WHERE 1=1

AND a.delNY = #{shDelNy}
AND a.grade = #{shOptionMem}

	AND a.dob BETWEEN #{shstartDate} AND #{shendDate}
	AND a.regDate BETWEEN #{shstartDate} AND #{shendDate}



	AND a.name LIKE CONCAT('%',#{shValue},'%')
	AND a.email LIKE CONCAT('%',#{shValue},'%')
	AND a.phone LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO member (
	seq
	, name
	, dob
	, membership
	, email
	, phone
	, regDate
	, delNY
)
VALUES (
	#{seq}
	, #{name}
	, #{dob}
	, #{membership}
	, #{email}
	, #{phone}
	, now()
	, 0
)

	SELECT last_insert_id()

mapper.update:
UPDATE Member
SET
	seq = #{seq}
	, name = #{name}
	, dob = #{dob}
	, membership = #{membership}
	, email = #{email}
	, phone = #{phone}
	, regDate = #{regDate}
WHERE
	seq = #{seq}
