mapper.sql:
FROM itemOrder a LEFT JOIN coupon d ON d.seq = a.coupon_seq LEFT JOIN member b ON b.seq = a.member_seq LEFT JOIN item c ON c.seq = a.item_seq WHERE 1=1
AND a.delNY = #{shDelNy}
AND a.orderdate BETWEEN #{shstartDate} AND #{shendDate}
AND b.name LIKE CONCAT('%',#{shValue},'%') AND b.id LIKE CONCAT('%',#{shValue},'%') AND b.addr1 LIKE CONCAT('%',#{shValue},'%') AND c.title LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO itemOrder (
	b.name
	, b.email
	, b.zipcode
	, b.addr1
	, b.addr2
	, b.addr3
	, a.spmessage
	, b.phone
	, c.title
	, c.price
	, a.count
	, d.cpnname
	, d.cpndiscount
	, d.cpnvalid
	, a.finalPrice
	, state
	, a.orderdate
)
VALUES (
	#{name}
	, #{email}
	, #{zipcode}
	, #{addr1}
	, #{addr2}
	, #{addr3}
	, #{spmessage}
	, #{phone}
	, #{title}
	, #{price}
	, #{count}
	, #{cpnname}
	, #{cpndiscount}
	, #{cpnvalid}
	, #{finalPrice}
	, 34
	, now()
)


	SELECT last_insert_id()

mapper.delete:
DELETE FROM itemOrder
WHERE 1=1
	AND seq = #{seq}
